package es.adama.archetype.configuration;import org.springframework.cache.CacheManager;import org.springframework.cache.annotation.EnableCaching;import org.springframework.cache.concurrent.ConcurrentMapCacheManager;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.EnableAspectJAutoProxy;import org.springframework.context.annotation.FilterType;import org.springframework.context.annotation.PropertySource;import org.springframework.context.support.PropertySourcesPlaceholderConfigurer;import org.springframework.scheduling.annotation.EnableAsync;import org.springframework.scheduling.annotation.EnableScheduling;import org.springframework.stereotype.Controller;import org.springframework.web.servlet.config.annotation.EnableWebMvc;/** *  * @author David Palomar * www.adamaconsulting.es * */@Configuration@ComponentScan(basePackages = { "es.adama.archetype" }, excludeFilters = {		@ComponentScan.Filter(value = Controller.class, type = FilterType.ANNOTATION),		@ComponentScan.Filter(value = EnableWebMvc.class, type = FilterType.ANNOTATION) })@EnableScheduling@EnableAspectJAutoProxy@EnableCaching@EnableAsync@PropertySource(value = { "classpath:application.properties" })class ApplicationConfig {		@Bean	public static PropertySourcesPlaceholderConfigurer placeHolderConfigurer()	{		return new PropertySourcesPlaceholderConfigurer();	}	@Bean	public CacheManager cacheManager() {		return new ConcurrentMapCacheManager();	}}